import WithAuth from "../components/auth/protected";
import app, { db } from "../lib/firebase";
import { MDBBtn, MDBIcon } from "mdbreact";
import { RiAddLine } from "react-icons/ri"
import { useEffect, useContext, useState } from "react";
import { AuthContext } from "../components/auth/auth";

const Home = props => {
    const { currentUser } = useContext(AuthContext);
    const [counter, setCounter] = useState(0);
    useEffect(() => {

        if (currentUser) {
                console.log(app.database().ref().child(currentUser.uid))
            const counterRef = app.database().ref().child(String(currentUser.uid)).child("counter");
            counterRef.on("value", snapshot => {
                console.log(snapshot);
                setCounter(snapshot);
            })
        }
    }, [currentUser])
    return (
        <WithAuth>
            <div className="d-flex flex-column gradient cover">
                <h2>
                    {currentUser ? currentUser.uid : null}
                </h2>
                <h1 style={{ fontSize: "40px", fontWeight: "500", color: "#ffffff" }} className="no-margin">
                    {counter}
                </h1>
                <h5 style={{ fontSize: "24px", color: "#ffffff" }} className="no-margin">
                    Contador
         </h5>
                <MDBBtn color="#f5f5f5" style={{ borderRadius: "15px", border: "none", margin: "0px" }}>
                    <RiAddLine style={{ fontSize: "72px", color: "#423ed3" }} />
                </MDBBtn>

            </div>
            <div style={{ height: "40vh", width: "100%" }} className="flex-c">

                <MDBBtn style={{ width: "90%", margin: '0px', }} onClick={() => { app.auth().signOut() }}>
                    Cerrar Sesi√≥n
            </MDBBtn>
            </div>

        </WithAuth>
    );
}

export default Home
