import { useForm } from 'react-hook-form';
import { useState, useRef } from 'react';
import app from '../lib/firebase';
import { useRouter } from 'next/router'
const Login = props => {

    const { register, handleSubmit, watch, errors } = useForm();
    const [signUpMode, setSignUpMode] = useState(false);
    const formRef = useRef();
    const router = useRouter()
    const onSubmit = async data => {
        console.log(data);
        if (signUpMode) {

            console.log("En signUp")
            setSignUpMode(false);
             try{
                await app.auth().createUserWithEmailAndPassword(data.email, data.password);

                router.push("/index")

            }catch (error){

            } 

        } else {
            console.log("Do login")
        }
    }

    const SignUp = () => {
        setSignUpMode(true);
         handleSubmit(formRef.current.submit)
    }
    return (
        <div >
            <form ref={formRef} style={{ display: "flex", flexDirection: "column", alignItems: "center", justifyContent: "center" }} onSubmit={handleSubmit(onSubmit)}>
                {/* register your input into the hook by invoking the "register" function */}
                <input placeholder="correo electrónico" name="email" ref={register} />
                <br></br>
                {/* include validation with required or other standard HTML validation rules */}
                <input placeholder="contraseña" name="password" ref={register({ required: true })} />
                <br></br>
                {/* errors will return when field validation fails  */}
                {errors.exampleRequired && <span>This field is required</span>}
                <input type="submit" />

                <button onClick={SignUp}>
                    Regsitrate
      </button>
            </form>

        </div>


    )
}

export default Login;
