import { useForm } from 'react-hook-form';
import { useState, useRef } from 'react';
import app from '../lib/firebase';
import { useRouter } from 'next/router'
import { MDBBtn } from 'mdbreact';

const InputStyle= {
    minWidth: "150px",
    border: ".5px solid #efefef",
    padding:" .5rem",
    width: "300px",
    height: "35px",
    fontSize: "15px",
    color: "#efefef"
}
const flex={
    display:"flex",
    justifyContent:"center",
    alignItems:"center",
    width:"100%",
    height:"100vh",
}
const Login = props => {

    const { register, handleSubmit, watch, errors } = useForm();
    const [signUpMode, setSignUpMode] = useState(false);
    const formRef = useRef();
    const router = useRouter()
    const onSubmit = async data => {
        console.log(data);
        if (signUpMode) {

            console.log("En signUp")
            setSignUpMode(false);
            try {
                await app.auth().createUserWithEmailAndPassword(data.email, data.password);
                router.push("/")
            } catch (error) {
                console.log(error)
            }
        } else {
            try {
                await app.auth().signInWithEmailAndPassword(data.email, data.password);
                router.push("/")
            } catch (error) {
                console.log(error)
            }
        }
    }

    const SignUp = () => {
        setSignUpMode(true);
        handleSubmit(formRef.current.submit)
    }
    return (
        <div style={flex} >
            <form ref={formRef} style={{ width:"100%",display: "flex", flexDirection: "column", alignItems: "center", justifyContent: "center" }} onSubmit={handleSubmit(onSubmit)}>
                {/* register your input into the hook by invoking the "register" function */}
                <input  style={InputStyle} placeholder="correo electrónico" name="email" ref={register()} />
                <br></br>
                {/* include validation with required or other standard HTML validation rules */}
                <input style={InputStyle} placeholder="contraseña" name="password" ref={register({ required: true })} />
                <br></br>
                {/* errors will return when field validation fails  */}
                {errors.exampleRequired && <span>This field is required</span>}

                <MDBBtn  style={{width:"317px"}} color="secondary" type="submit">Iniciar Sesión</MDBBtn>

                <MDBBtn style={{width:"317px", marginTop:"50px"}} color="secondary" onClick={SignUp}>
                    Crear Cuenta
              </MDBBtn>
            </form>
        </div>
    )
}

export default Login;
