import { useForm } from 'react-hook-form';
import { useState, useRef, useContext } from 'react';
import app from '../lib/firebase';
import { useRouter } from 'next/router'
import { MDBBtn } from 'mdbreact';
import { AuthContext } from '../components/auth/auth';
import { useFormik } from 'formik';

const InputStyle = {
    minWidth: "150px",
    border: ".5px solid #efefef",
    padding: " .5rem",
    width: "300px",
    height: "35px",
    fontSize: "15px",
    color: "#efefef"
}
const flex = {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    width: "100%",
    height: "100vh",
    flexDirection: "column"
}

const validate = values => {
    const errors = {};
    if (!values.password) {
      errors.password = 'Requerido';
    } else if (values.password.length < 5) {
      errors.password = 'Debe ser de almenos 5 caracteres';
    }

    if (!values.email) {
      errors.email = 'Requerido';
    } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.email)) {
      errors.email = 'Email invalidos';
    }

    return errors;
  };


const Login = props => {

    const { register, handleSubmit, watch, errors } = useForm();
    const [signUpMode, setSignUpMode] = useState(false);
    const formRef = useRef();
    const router = useRouter()
    const { currentUser } = useContext(AuthContext);

    const submitForm = async (data) => {
        if (signUpMode) {
            setSignUpMode(false);
             app.auth().createUserWithEmailAndPassword(data.email, data.password).then(user=>{

                console.log(user);
                  /*   let id = currentUser.uid;
                    let newObject = {};
                    newObject[id] = { counter: 0 }
                    await app.database().ref().set(newObject)                    
                    router.push("/") */
                }).catch(error=>{
                    console.log(error);
                })

        } else {
            try {
                await app.auth().signInWithEmailAndPassword(data.email, data.password);
                router.push("/")
            } catch (error) {
                console.log(error)
            }
        }
    }
    const formik = useFormik({
        initialValues: {
          email: '',
          password:'',
        },
        validate,
        onSubmit: values => {
            submitForm(values);
        },});

    const SignUp = () => {
        setSignUpMode(true);
        formik.handleSubmit();
    }
    return (
        <div style={flex} >
            <div style={{ textAlign: "left", width: "100%", paddingLeft: "25px", marginBottom: "40px" }}>
                <h1 style={{ fontWeight: "500", color: "#4B515D", marginBottom: "5px" }}>
                    We are <span style={{ color: "#4239cd" }}>
                        Lernit
                    </span>
                </h1>

                <p style={{ marginTop: "0px" }}>
                    Mejora tu experiencia de aprendizaje
                </p>
            </div>
            <form ref={formRef} style={{ width: "100%", display: "flex", flexDirection: "column", alignItems: "center", justifyContent: "center" }} onSubmit={formik.handleSubmit}>

                <input 
                value={formik.values.email}
                onChange={formik.handleChange}
                style={InputStyle} placeholder="correo electrónico" name="email" ref={register()} />
                <br></br>
                {formik.errors.email ? <div>{formik.errors.email}</div> : null}

                <input 
                  value={formik.values.password}
                  onChange={formik.handleChange}
                style={InputStyle} placeholder="contraseña" name="password" ref={register({ required: true })} />
                <br></br>

                {formik.errors.password && <div>{formik.errors.password}</div>}

                <MDBBtn style={{ width: "317px", background: "#4B515D" }} type="submit">Iniciar Sesión</MDBBtn>

                <MDBBtn style={{ width: "317px", marginTop: "40px", background: "#4B515D" }} onClick={SignUp}>
                    Crear Cuenta
              </MDBBtn>
            </form>
        </div>
    )
}

export default Login;
